func countFloors(problem_input string, exit_early bool) (floor int, step int) {
	floor = 0
	for step, char := range problem_input {
		if char == '(' {
			floor += 1
		} else {
			floor -= 1
		}

		if floor == -1 && exit_early {
			return floor, step + 1
		}
	}

	return floor, step + 1
}

func main() {
	final, _ := countFloors(problem_input, false)
	fmt.Printf("You finished on floor %d\n", final)

	_, step := countFloors(problem_input, true)
	fmt.Printf("You reached the basement after %d steps\n", step)
}
